# input: N(jewelry), K(backpack), M(mass),V(price),C(volume of backpack)
# output: maximum price

# import
import sys
import heapq
# Input
N, K = map(int, sys.stdin.readline().split())
jew=[]
bags=[]

for _ in range(N):
    heapq.heappush(jew, list(map(int, sys.stdin.readline().split())))
for _ in range(K):                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
    bags.append(int(sys.stdin.readline()))
bags.sort()

answer = 0

jew_pos = []
for mass in bags:
    while jew and mass >= jew[0][0]:
        heapq.heappush(jew_pos, -heapq.heappop(jew)[1])
    if jew_pos:
        answer -= heapq.heappop(jew_pos)
    elif not jew:
        break

print(answer)